package Use_All_Data_Types;

public class Calculations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int a, b;
	 a = 200;
	 b = 50;
	
	
	System.out.println("Answer is: " + a+b);
	System.out.println("Answer is: " + a*b);
	System.out.println("Answer is: " + a/b);
	System.out.println("Answer is: " + a%b);
	System.out.println("Answer is: " + (a-b));
		
		
	}

}
